  public void doTeleop(Joystick stick) {
    robotDrive21.arcadeDrive(stick,true);
    SmartDashboard.putNumber("Left Encoder", this.getLeftDistance());
    SmartDashboard.putNumber("Right Encoder", this.getRightDistance());
  }

  public void driveForward(double speed) {
    // speed is 0 stopped to 1 full speed
    speed = Math.abs(speed);
    if(speed > 1){
      speed = 1;
      }
    robotDrive21.arcadeDrive(-speed, 0);
  }

  public void driveBackward(double speed) {
    // speed is 0 stopped to 1 full speed
    speed = Math.abs(speed);
    if(speed > 1){
      speed = 1;
      }
    robotDrive21.arcadeDrive(speed, 0);
  }
  
  public void zeroEncoders(){
    this.leftEncoder.reset();
    this.rightEncoder.reset();
  }
  
  public void turnRight(double speed){
    // speed is 0 stopped to 1 full speed
    speed = Math.abs(speed);
    if(speed > 1){
      speed = 1;
      }
    robotDrive21.tankDrive(-speed, speed);
  }
  
  public void turnLeft(double speed){
    // speed is 0 stopped to 1 full speed
    speed = Math.abs(speed);
    if(speed > 1){
      speed = 1;
      }
    robotDrive21.tankDrive(speed, -speed);  
  }
  
  public double getLeftDistance(){
    return this.leftEncoder.getDistance();
  }
  
  public double getRightDistance(){
    return this.rightEncoder.getDistance();
  }

  public double getAverageDistance() {
    return (rightEncoder.getDistance() + leftEncoder.getDistance()) / 2 ;
  }
  
  public void stop() {
    robotDrive21.arcadeDrive(0, 0);
  }

